export default Test;


function Post(props) {
    const { title, content, author, date } = props;
    const jsonData = [
        { id: 1, title: "First Post", content: "This is the first post.", author: "John Doe", date: "2023-03-04" },
        { id: 2, title: "Second Post", content: "This is the second post.", author: "Jane Smith", date: "2023-03-05" },
        { id: 3, title: "Third Post", content: "This is the third post.", author: "Bob Johnson", date: "2023-03-06" },
      ];
      
     
    return (
    <div className="post">
        <h2 className="post-title">{title}</h2>
        <p className="post-content">{content}</p>
        <div className="post-meta">
        <span className="post-author">Author: {author}</span>
        <span className="post-date">Date: {date}</span>
        </div>
    </div>
    );
}
    
    function PostList(props) {
        const { data } = props;
    
        return (
        <div className="post-list">
            {data.map((item) => (
            <Post
                key={item.id}
                title={item.title}
                content={item.content}
                author={item.author}
                date={item.date}
            />
            ))}
        </div>
        );
}
