import React, { useState, useEffect } from 'react';
import './addproduct.css';
import {  Link } from 'react-router-dom';
import axios from 'axios';





function AddProduct() {


    const [selectedProduct, setSelectedProduct] = useState();

    const handleChange = (event) => {
        setSelectedProduct(event.target.value);
        
    };
    
    
    const [productInfo, setProductInfo] = useState({
        sku: '',
        name: '',
        price: '',
        type: '',
        attributeType:'',
        attributeValue:''
    });
    
    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setProductInfo(prevState => ({
            ...prevState,
            [name]: value
            
        }));
    };

    

    const handleSubmit = (event) => {
        event.preventDefault();
        productInfo.type = selectedProduct;
        
        console.log(productInfo);
    }

    const DVD = () =>{
        return(
            <div className='inputContainerType'>
                <div  className='inputName'>
                    <p>Size(MB)</p>
                    <input id='size' type="text" />
                </div>
                <div className='example'>
                    <p>Please provide the size in Megabytes.</p>
                </div>
            </div>
        )
    }
    const Book = () =>{
        return(
            <div className='inputContainerType'>
                <div  className='inputName'>
                    <p>Weight(KG)</p>
                    <input id='weight' type="text" />
                </div>
                <div className='example'>
                    <p>Please provide the weight in Kg.</p>
                </div>
            </div>
        )
    }
    const Furniture = () =>{
        const Furniture = () => {
            const handleDimensionChange = (event) => {
                const { name, value } = event.target;
                setProductInfo(prevState => ({
                    ...prevState,
                    typeAttribute: {
                        ...prevState.typeAttribute,
                        [name]: value
                    }
                }));
            };
        
            useEffect(() => {
                setProductInfo(prevState => ({
                    ...prevState,
                    attributeValue: `${prevState.typeAttribute.length}x${prevState.typeAttribute.width}x${prevState.typeAttribute.height}`
                }));
            }, [productInfo.typeAttribute]);
        
            return (
                <div className='inputContainerType'>
                    <div  className='inputName'>
                        <p>Height (CM)</p>
                        <input id='height' type="text" name='height' value={productInfo.typeAttribute.height} onChange={handleDimensionChange} />
                    </div>
                    <div  className='inputName'>
                        <p>Width (CM)</p>
                        <input id='width' type="text" name='width' value={productInfo.typeAttribute.width} onChange={handleDimensionChange} />
                    </div>
                    <div  className='inputName'>
                        <p>Length (CM)</p>
                        <input id='length' type="text" name='length' value={productInfo.typeAttribute.length} onChange={handleDimensionChange} />
                    </div>
                    <div className='example'>
                        <p>Please provide dimensions in cm.</p>
                    </div>
                </div>
            );
        }
    }



    const renderComponent = () => {
        switch (selectedProduct) {
            case "DVD":
            return <DVD/>
            case "Book":
            return <Book/>
            case "Furniture":
            return <Furniture/>;
            default:
            return null;
        }
    };
        
    console.log(selectedProduct);

    return (
    <div className='container'>
        <div className='headerProductList'>
            <div className='ProductList'>
                <h2>Product Add</h2>
            </div>
            
            <div className='buttonsContainer'>
                <div className='buttons'>
                    <input id='save' type='submit' form='formProduct' value={'Save'}></input>
                </div>
                <div className='buttons'>
                    <Link to='/'  id='cancel'>
                        <p className='buttonsName'>Cancel</p>
                    </Link>
                </div>
            </div>
        </div>
        {/* ------------------------------------------------------------------------------------- */}
        <div className='containerAdd'>
            <form id='formProduct' className='formContainer' onSubmit={handleSubmit} >
                <label>
                    <div  className='inputName'>
                        <p>SKU</p>
                        <input id='sku' type="text" name="sku" value={productInfo.sku} onChange={handleInputChange}  />
                    </div>
                    <div  className='inputName'>
                        <p>Name</p>
                        <input id='name' type="text" name="name" value={productInfo.name} onChange={handleInputChange} />
                    </div>
                    <div  className='inputName'>
                        <p>Price($)</p>
                        <input id='price' type="text" name="price" value={productInfo.price} onChange={handleInputChange} />
                    </div>
                    
                </label>
                <div className='dropdownSwitch'>
                <label htmlFor="product-selector">Type Switcher</label>
                    <select  id='productType' value={selectedProduct} onChange={handleChange}>
                        <option value="">Select an option</option>
                        <option value="DVD">DVD</option>
                        <option value="Book">Book</option>
                        <option value="Furniture">Furniture</option>
                    </select>
                </div>
                {renderComponent()}
            </form>
        </div>
    </div>
    )
}
export default AddProduct
