import React, { useState, useEffect } from 'react';
import {  Link } from 'react-router-dom';
import axios from 'axios';
import './productpage.css';

function ProductCheckbox({ product }) {

    
    const [checkedIds, setCheckedIds] = useState([]);
    const handleCheckboxChange = (event, product) => {
        const { checked } = event.target;
        const productId= event.target.value;

        if (checked) {
        setCheckedIds((prevState) => [...prevState, product.id]);
        } else {
        setCheckedIds((prevState) =>
            prevState.filter((id) => id !== product.id)
        );
        }
    }

}

    // const deleteProducts = async (productIds) => {
    //     try {
    //     const response = await axios.post('http://localhost/deleteproducts.php', { productIds });
    //     console.log(response.data);
    //     // Navigate back to home page or do any other necessary action
    //     } catch (error) {
    //     console.error(error);
    //     }
    // };

function ProductPage() {

    const [checkedIds, setCheckedIds] = useState([]);
    const handleCheckboxChange = (event, productId) => {
        const { checked } = event.target;
    
        if (checked) {
            setCheckedIds((prevState) => [...prevState, productId]);
        } else {
            setCheckedIds((prevState) =>
            prevState.filter((id) => id !== productId)
            );
        }
    };

    function handleDelete() {
        // Send delete request with checkedIds
        console.log('checkedIds');
    }

    




    const [products, setProducts] = useState([]);
    useEffect(() => {
        axios.get('http://localhost/products.php')
        .then(response => {
            setProducts(response.data);
        })
        .catch(error => {
            console.log(error);
        });
    }, []);
    console.log(products);
    return (
    <div className='container'>
        <div className='headerProductList'>
            <div className='ProductList'>
                <h2>Product List</h2>
            </div>
            
            <div className='buttonsContainer'>
                <div className='buttons'>
                    <Link to='/add-product'  id='add-product-btn'>
                        <p className='buttonsName'>ADD</p>
                    </Link>
                </div>
                <div className='buttons'>
                    <button id='delete-product-btn' onClick={handleDelete}>
                        <p>MASS DELETE</p>
                    </button>
                </div>
            </div>
        </div>
        {/* ------------------------------------------------------------------------------------- */}
        <div className='containerProducts'>
            {products.map(product => (
            <div key={product.id} className='containerProduct'>
                <div  className='containerProduct2'>
                    <div className='checkboxDiv'>
                    <input type="checkbox" value={product.id} onChange={(event) => handleCheckboxChange(event, product.id)}
/>
                    </div>
                    <p className='sku'>Sku: {product.sku}</p>
                    <p className='name'>Name:{product.name}</p>
                    <p className='price'>Price: {product.price}$</p>
                    <p className='attributes'>{product.attribute_type}: {product.attribute_value}</p>
                </div>
            </div>
            ))}
        </div>
    </div>
            
    )
}

export default ProductPage;
