import React, { useState, useEffect } from 'react';
import './addproduct.css';
import {  Link } from 'react-router-dom';
import axios from 'axios';



const DVD = () =>{
    return(
        <div className='inputContainerType'>
            <div  className='inputName'>
                <p>Size(MB)</p>
                <input id='size' type="text" />
            </div>
            <div className='example'>
                <p>Please provide the size in Megabytes.</p>
            </div>
        </div>
    )
}
const Book = () =>{
    return(
        <div className='inputContainerType'>
            <div  className='inputName'>
                <p>Weight(KG)</p>
                <input id='weight' type="text" />
            </div>
            <div className='example'>
                <p>Please provide the weight in Kg.</p>
            </div>
        </div>
    )
}
const Furniture = () =>{
    return(
        <div className='inputContainerType'>
            <div  className='inputName'>
                <p>Height (CM)</p>
                <input id='height' type="text" />
            </div>
            <div  className='inputName'>
                <p>Width (CM)</p>
                <input id='width' type="text" />
            </div>
            <div  className='inputName'>
                <p>Length (CM)</p>
                <input id='length' type="text" />
            </div>
            <div className='example'>
                <p>Please provide dimensions in cm.</p>
            </div>
        </div>
    )
}

function AddProduct() {


    const [selectedProduct, setSelectedProduct] = useState();

    const handleChange = (event) => {
        setSelectedProduct(event.target.value);
    };
    
    
    const [productInfo, setProductInfo] = useState({key:"value",key2:""});
    console.log(productInfo);

    const handleSubmit = (event) => {
        event.preventDefault();
        alert({productInfo})
    }

    const renderComponent = () => {
        switch (selectedProduct) {
            case "DVD":
            return <DVD/>
            case "Book":
            return <Book/>
            case "Furniture":
            return <Furniture/>;
            default:
            return null;
        }
    };
        
    console.log(selectedProduct);

    return (
    <div className='container'>
        <div className='headerProductList'>
            <div className='ProductList'>
                <h2>Product Add</h2>
            </div>
            
            <div className='buttonsContainer'>
                <div className='buttons'>
                    <input id='save' type='submit' form='formProduct' value={'Save'}></input>
                </div>
                <div className='buttons'>
                    <Link to='/'  id='cancel'>
                        <p className='buttonsName'>Cancel</p>
                    </Link>
                </div>
            </div>
        </div>
        {/* ------------------------------------------------------------------------------------- */}
        <div className='containerAdd'>
            <form id='formProduct' className='formContainer' onSubmit={handleSubmit} >
                <label>
                    <div  className='inputName'>
                        <p>SKU</p>
                        <input id='sku' type="text" value={productInfo.sku} onChange={(e)=> setProductInfo(e.target.value)}  />
                    </div>
                    <div  className='inputName'>
                        <p>Name</p>
                        <input id='name' type="text"  />
                    </div>
                    <div  className='inputName'>
                        <p>Price($)</p>
                        <input id='price' type="text"  />
                    </div>
                    
                </label>
                <div className='dropdownSwitch'>
                <label htmlFor="product-selector">Type Switcher</label>
                    <select  id='productType' value={selectedProduct} onChange={handleChange}>
                        <option value="">Select an option</option>
                        <option value="DVD">DVD</option>
                        <option value="Book">Book</option>
                        <option value="Furniture">Furniture</option>
                    </select>
                </div>
                {renderComponent()}
            </form>
        </div>
    </div>
    )
}
export default AddProduct
