import React, { useState, useEffect } from 'react';
import axios from 'axios';


function Test() {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        axios.get('http://localhost/products.php')
        .then(response => {
            setProducts(response.data);
        })
        .catch(error => {
            console.log(error);
        });
    }, []);
    console.log(products);
    
    return (
        <ProductList/>
