        // </ul>
        // </div>


    )
}

export default Test;


function ProductList(props) {
    const { products } = props;
  
    const productContainers = renderProductContainers(products);
  
    return (
      <div className="productList">
        {productContainers}
      </div>
    );
  }




  function renderProductContainers(products) {
    return products.map(product => {
      const { sku, name, price, type, typeValue } = product;
  
      return (
        <ProductContainer
          sku={sku}
          name={name}
          price={price}
          type={type}
          typeValue={typeValue}
          key={sku} // using sku as the key assuming it's unique
        />
      );
    });
  }
