        //     </li>
        //     ))}
        // </ul>
        // </div>


    )
}

export default Test;


function Post(props) {
    const { title, content, author, date } = props;
    
      
     
    return (
    <div className="post">
        <h2 className="post-title">{title}</h2>
        <p className="post-content">{content}</p>
        <div className="post-meta">
        <span className="post-author">Author: {author}</span>
        <span className="post-date">Date: {date}</span>
        </div>
    </div>
    );
}
    
    function PostList(props) {
        const { data } = props;
    
        return (
        <div className="post-list">
            {data.map((item) => (
            <Post
                key={item.id}
                title={item.title}
                content={item.content}
                author={item.author}
                date={item.date}
            />
            ))}
        </div>
        );
}
