        //     </li>
        //     ))}
        // </ul>
        // </div>


    )
}

export default Test;


function Post(props) {
    const{sku,name,price,type,typeValue} = props;
    
      
     
    return (
    <div className="postContainer">
        <h2 className="postSku">{sku}</h2>
        <p className="postName">{name}</p>
        <p className='postPrice'>{price}</p>
        <div>
            <span className="post-date">{type}</span>
            <span className="post-date">{typeValue}</span>
        </div>
    </div>
    );
}
    
    function PostList(props) {
        const { data } = props;
    
        return (
        <div className="post-list">
            {data.map((item) => (
            <Post
                key={item.id}
                sku={item.sku}
                name={item.name}
                price={item.price}
                type={item.type}
                typeValue={item.typeValue}
            />
            ))}
        </div>
        );
}
